dtstptitle.validator:
function(value) {
    var dialog = this.findParentByType('dialog');
 var dtstptitle = dialog.getField('./dtstptitle')
 var dtstptitlevalue = dtstptitle.getValue();
 if($.trim(dtstptitlevalue)==""){
  return false;
 }else{
  return true;
 }
}

