locimagealttext.validator:
function(value) {
    var dialog = this.findParentByType('dialog');
 var locimagealttext = dialog.getField('./jcr:locimagealttext')
 var locimagealttextvalue = locimagealttext.getValue();
 if($.trim(locimagealttextvalue)==""){
  return false;
 }else{
  return true;
 }
}

