noofitemsshown.validator:
function(value) {
    var dialog = this.findParentByType('dialog');
 var bannerimage1 = dialog.getField('./noofitemsshown')
 var bannerimage1value = bannerimage1.getValue();
 if($.trim(bannerimage1value)==0)
 {
 alert("No. of news Shown must be greater than Zero");
 return false;
 }
else if($.trim(bannerimage1value)==""){
  return false;
 }else{
  return true;
 }
}

