			 function(value) {
    var dialog = this.findParentByType('dialog');
 var newsPathField = dialog.getField('./readmoretext')
 var newsPath = newsPathField.getValue();
 if($.trim(newsPath)==""){
  return false;
 }else{
  return true;
 }
}
