ppbbheadline.validator:
function(value) {
    var dialog = this.findParentByType('dialog');
 var newsPathField = dialog.getField('./jcr:ppbbheadline')
 var ppbbheadline = newsPathField.getValue();
 if($.trim(ppbbheadline)==""){
  return false;
 }else{
  return true;
 }
}

