pressreleaseslabel.validator:
function(value) {
    var dialog = this.findParentByType('dialog');
 var bannerimage1 = dialog.getField('./pressreleaseslabel')
 var bannerimage1value = bannerimage1.getValue();
 if($.trim(bannerimage1value)==""){
  return false;
 }else{
  return true;
 }
}

