prstptitle.validator:
function(value) {
    var dialog = this.findParentByType('dialog');
 var prstptitle = dialog.getField('./prstptitle')
 var prstptitlevalue = prstptitle.getValue();
 if($.trim(prstptitlevalue)==""){
  return false;
 }else{
  return true;
 }
}

